import React from 'react';
import './Hdr.css';

const Hdr = ({ onAddUserClick }) => {
  const imageUrl = 'https://example.com/path/to/your/image.jpg'; // Replace with your absolute image URL

  const hdrStyle = {
    backgroundImage: `url(${imageUrl})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'relative',
  };

  const buttonStyle = {
    position: 'absolute',
    top: '50%', // Adjust as needed
    left: '50%', // Adjust as needed
    transform: 'translate(-50%, -50%)',
  };

  return (
    <div className="hdr" style={hdrStyle}>
      <h1>cinema review</h1>
      <button onClick={onAddUserClick} className="add-user-button" style={buttonStyle}>
        Write a review!
      </button>
    </div>
  );
};

export default Hdr;
