// App.js
// import './App.css'; // Comment out or remove this line
import React, { useState } from 'react';
import AddUser from './AddUser';
import Hdr from './Hdr';
import UsersList from './UsersList';
import './styles.css';

function ShowItemList() {
  const [users, setUsers] = useState([
    {
      username: 'Guest1',
      rating: 4,
      img: 'https://rlterryreelview.files.wordpress.com/2023/05/acrossspiderverse_1.jpeg',
      description: 'A great movie!',
      movieTitle: 'Across the Spiderverse',
    },
    {
      username: 'LuanHo1',
      rating: 5,
      img: 'https://m.media-amazon.com/images/M/MV5BNjU3N2QxNzYtMjk1NC00MTc4LTk1NTQtMmUxNTljM2I0NDA5XkEyXkFqcGdeQXVyODE5NzE3OTE@._V1_.jpg',
      description: 'I hope my friends do not judge me for this... I like it more than Oppenheimer',
      movieTitle: 'Barbie',
    },
    {
      username: 'Krietster',
      rating: 4,
      img: 'https://upload.wikimedia.org/wikipedia/en/f/ff/The_Batman_%28film%29_poster.jpg',
      description: 'hmmm I like it a lot more than any Marvel movie!!!',
      movieTitle: 'Batman',
    },
  ]);

  const addUserHandler = (userData) => {
    setUsers((prevUsers) => {
      return [...prevUsers, userData];
    });
  };

  const handleAddUserClick = () => {
    // Define the behavior when the "Add User" button is clicked
    console.log('Add User button clicked!');
  };

  return (
    <div>
      <Hdr onAddUserClick={handleAddUserClick} />

      <div className="body-content">
        {/* Body Content Goes Here */}
      </div>

      <div className="card-section">
        <AddUser onAddUser={addUserHandler} />
        <UsersList users={users} />
      </div>
    </div>
  );
}

export default ShowItemList;
