import React, { useState } from 'react';
import Card from './Card';
import Button from './Button';
import './AddUser.css';
import { FaStar } from "react-icons/fa";

const colors = {
  orange: "#FFBA5A",
  grey: "#a9a9a9"
};


const AddUser = (props) => {
  const [newItem, setNewItem] = useState({
    username: '',
    rating: 0,
    img: '',
    description: '',
    movieTitle: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewItem((prevItem) => ({
      ...prevItem,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Submitted data:', newItem);

    setNewItem({
      username: '',
      rating: 0,
      img: '',
      description: '',
      movieTitle: '',
    });
  };

  return (
    <Card className="input">
      <form onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          type="text"
          name="username"
          value={newItem.username}
          onChange={handleChange}
        />
        <label>Movie Title</label>
        <input
          type="text"
          name="movieTitle"
          value={newItem.movieTitle}
          onChange={handleChange}
        />
        <label>Rating</label>
        <div style={styles.stars}>
          {[...Array(5)].map((_, index) => (
            <FaStar
              key={index}
              size={24}
              onClick={() => setNewItem((prevItem) => ({ ...prevItem, rating: index + 1 }))}
              color={(newItem.rating > index) ? colors.orange : colors.grey}
              style={{
                marginRight: 10,
                cursor: "pointer"
              }}
            />
          ))}
        </div>
        <label>Link to image</label>
        <input
          type="text"
          name="img"
          value={newItem.img}
          onChange={handleChange}
        />
        <label>Review Description</label>
        <input
          type="text"
          name="description"
          value={newItem.description}
          onChange={handleChange}
        />
        <Button type="submit">Submit</Button>
      </form>
    </Card>
  );
};

const styles = {
  stars: {
    display: "flex",
    flexDirection: "row",
  }
};

export default AddUser;
