import React from 'react';
import './Hdr.css';

const Hdr = ({ onAddUserClick }) => {
  const imageUrl = 'https://cdn.discordapp.com/attachments/341337244927524873/1176193564905050142/DSSS.png?ex=656dfa9e&is=655b859e&hm=9d5141bad69b979a750d10698e60277f8045ea8a016352e1695d5181b00a1052&'

  const hdrStyle = {
    position: 'relative',
  };

  const imageStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%', // Adjust as needed
    height: '100%', // Adjust as needed
    objectFit: 'cover', // Ensures the image covers the entire container
    zIndex: -1, // Places the image behind the content
  };

  return (
    <div className="hdr" style={hdrStyle}>
      <img src={imageUrl} alt="Background" style={imageStyle} />
      <h1>cinema review</h1>
      <button onClick={onAddUserClick} className="add-user-button">
        Write a review!
      </button>
    </div>
  );
};

export default Hdr;
