import React, { useState } from 'react';
import User from './components/User';
import AddUser from './components/AddUser';
import Hdr from './components/Hdr';
import Button from './components/Button'; // Import the Button component

function App() {
  const [users, setUsers] = useState([]);
  const [isAddUserVisible, setAddUserVisible] = useState(false);

  const addUserHandler = (newUser) => {
    setUsers((prevUsers) => [...prevUsers, newUser]);
    setAddUserVisible(false);
  };

  return (
    <div>
      <Hdr onAddUserClick={() => setAddUserVisible(true)} />
      {isAddUserVisible && <AddUser onAddUser={addUserHandler} />}
      {users.map((user, index) => (
    <User
      key={index}
      name={user.name}
      age={user.age}
      image={user.image}
      major={user.major}
    />
  ))}
      <Button onClick={() => setAddUserVisible(true)}>Add User</Button> {/* Use the Button component */}
    </div>
  );
}

export default App;
