import { useEffect } from "react";
import { Route, BrowserRouter as Router, Routes } from "react-router-dom";
import CreateItem from "./components/CreateItem";
import Login from "./components/Login";
import ShowAuthenticatedView from "./components/ShowAuthenticatedView";
import ShowItemList from "./components/ShowItemList";
import Signup from "./components/Signup";
import UserContext from "./context/UserContext";

const App = () => {
    const [userData, setUserData] = useState({
        token: undefined,
        user: undefined,
    });
    useEffect(() => {
        const checkLoggedIn = async() => {
            let token = localStorage.getItem("auth-token");
            if (token === null) {
                localStorage.setItem("auth-toekn", "");
                token = "";
            }
            const tokenResponse = await axios.post(
                "http://localhost:4000", {
                    headers: { "x-auth-token": token },

            });
            setUserData({
                token,
                user: userRes.data,
            });
        }
        checkLoggedIn();
    }, []);

    return (
        <UserContext.Provider value={{ userData, setUserData }}>
        <Router>
            <div>
                <Routes>
                    <Route exact path = '/' element={ <ShowItemList/> } />
                    <Route exact path = '/create-item' element={<CreateItem />} />
                    <Route exact path = '/authenticated' element={ <ShowAuthenticatedView /> } />
                    <Route exact path = '/login' element={ <Login />} />
                    <Route exact path = '/signup' element={ <Signup/> } />
                </Routes>
            </div>
        </Router>
        </UserContext.Provider>
    )
}

export default App;
